def create_hash(string)
  hash = Hash.new
  string.each_char { |c| hash[c] = 0 }
  hash
end

def count_chars(text, hash)
  arr = text.chars.map { |c| hash[c] = hash[c] + 1 }
  hash
end

def sort_letters(hash, array)
  ordered = hash.values.sort! { |x, y| y <=> x }
  ordered.each { |v| array << hash.key(v) }
  p array.join
end

